package Incdec;

public class IncDec {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int x = 10;
		System.out.println("a and b value before the operations: "
		+ a + " " + b);
		++a;
		int c = ++a +  a--;
		System.out.println("c value after the operations: " + c);
		for (int i = 5; i < 10; i++) {
			if (i == 5)
			break;
			System.out.println(i);
	}

}
}
